import axios from "axios"
import { useState } from "react"
import { useNavigate } from "react-router"
import { toast } from "react-toastify"
import AdminHeader from "../../../../components/AdminHeader/adminHeader"
import { URL } from "../../../../config"

const AddCuisine = () => {
    const [name, setName] = useState('')
    const [image, setImage] = useState('')
    const [description, setDescription] = useState('')

    const navigate = useNavigate()

    const addCuisine = () => {
        const body = {
            name,
            image,
            description,
        }

        const url = `${URL}/admin/addcuisine`

        axios.post(url, body).then((response) => {
            const result = response.data

            // console.log(result)

            if (result['status'] === 'success') {
                toast.success('signup success')
                navigate('/cuisinesAll')
            }
            else {
                toast.error(result['error'])
            }
        })
    }
    return (
        <div className="container">
            <AdminHeader></AdminHeader>
            <div className="main-div">
                <label className="emp-details">Add New Cuisine</label>
                <div className="mb-3">
                    <label  className="form-label">Cuisine Name</label>
                    <input type="text" className="form-control" placeholder="cuisine name"></input>
                </div>
                <div className="mb-3">
                    <label  className="form-label">Cuisine Image Link</label>
                    <input type="text" className="form-control" placeholder="image"></input>
                </div>
                <div className="mb-3">
                    <label className="form-label">Cuisine Description</label>
                    <textarea className="form-control" placeholder="description" rows="3"></textarea>
                </div>
            </div>
        </div>
    )
}

export default AddCuisine
