                            />
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputlastName">Last Name</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputlastName" 
                            value="Kumkar" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                    </div>
                    <br/>
                        <div className="row">
                        <div className="form-group col-md-5">
                            <label for="inputEmail">Email</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputEmail" 
                            value="abhijeet09.ak@gmail.com" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputPhoneNumber">Phone Number</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputPhoneNumber" 
                            value="9970106353" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        </div>
                        <br />
                        <div className="row">
                        <div className="form-group col-md-5">
                            <label for="inputBirthDate">Birth Date</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputBirthDate" 
                            value="09-11-1997" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputGender">Gender</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputGender" 
                            value="Male" 
                            aria-label="Disabled input example" disabled readonly
                            /> 
                        </div>
                        </div>
                        <br/>
                        <div className="row">
                        <div className="form-group col-10">
                        <label for="inputAddress">Address</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputAddress" 
                            value="Housing Colony Pravaranagar" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        </div>
                        <br/>
                        <div className="row">
                        <div className="form-group col-md-5">
                            <label for="inputJoinDate">Join Date</label>
