                            type="text" 
                            id="inputJoinDate" 
                            value="25-05-2021" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputVehicleNumber">Vehicle Number</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputVehicleNumber" 
                            value="MH17-AB-8252" 
                            aria-label="Disabled input example" disabled readonly
                            /> 
                        </div>
                        </div>
                        <br/>
                        
                        <div className="row">
                        <div className="form-group col-md-5">
                            <label for="inputStatus">Status</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputStatus" 
                            placeholder="Confirmed"
                            aria-describedby="button-addon2" 
                            />
                            <button class="btn btn-success" type="button" id="button-addon2" onClick={saveStatus}>Save</button>
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputSalary">Salary</label>
                            <input
                            class="form-control" 
                            type="number" 
                            id="inputSalary" 
                            placeholder="15000"
                            aria-describedby="button-addon2"
                            />
                            <button class="btn btn-success" type="button" id="button-addon2" onClick={saveSalary}>Save</button>
                        </div>
                        </div>
                    
                    </form>

            </div>



        </div>
    )
}
export default EmpDetails























