import axios from "axios"
import { useEffect, useState } from "react"
import { toast } from "react-toastify"
import AdminHeader from "../../../../components/AdminHeader/adminHeader"
import { URL } from "../../../../config"
import './cuisinesAll.css'

const CuisinesAll = () => {
    const [cuisines, setCuisines] = useState([])

    const getCuisines = () => {

        const url = `${URL}/user/cuisines`

        axios.get(url).then((response) => {
            const result = response.data
            // console.log(result.data)
            if (result.status === 'success') {
                setCuisines(result.data)
            } else {
                console.log(result.error)
                toast.error(result['error'])
            }
        })
    }

    useEffect(() => {
        getCuisines()
    }, [])

    return <div className="container">
        <AdminHeader></AdminHeader>
        <div className="main-div">
            <label className="emp-details">All Cuisines</label>
            {
                cuisines.map((cuisine) => {
                    return (
                        <div>
                            <img src={cuisine.image} className="rounded float-start image-size" alt={cuisine.name}></img>
                            <label className="name-cuisine">{cuisine.name}</label>
                        </div>
                    )
                })
            }
        </div>
    </div>
}

export default CuisinesAll
