import axios from "axios"
import { useEffect, useState } from "react"
import { useLocation } from "react-router"
import { toast } from "react-toastify"
import AdminHeader from "../../../../components/AdminHeader/adminHeader"
import { URL } from "../../../../config"

const EmpAllOrders = () => {
    const [allOrders, setAllorders] = useState([])
    const { state } = useLocation()

    const { empId, empName } = state

    const getAllOrders = () => {
        const url = `${URL}/employee/order/history/${empId}`

        axios.get(url).then((response) => {
            const result = response.data
            if (result.status === 'success') {
                setAllorders(result.data)
                console.log(result.data)
            } else {
                console.log(result.error)
                toast.error(result['error'])
            }
        })
    }

    useEffect(() => {
        getAllOrders()
    }, [])

    return <div className="container">
        <AdminHeader></AdminHeader>
        <div className="main-div">
        <label className="emp-details">All Orders</label>
            {allOrders.length < 1 &&
                <div className="text-center fs-3 fw-light">There is no Order to show</div>
            }
            {allOrders.length > 0 &&
                <table class="table">
                    <thead class="table-dark">
                        <tr>
                            <td>Order Id</td>
                            <td>Employee Name</td>
                            <td>Gender</td>
                            <td>Email</td>
                            <td>Phone No</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            employees.map((employee) => {
                                return (
                                    <tr onClick={() => {
                                        navigate('/empDetails', { state: { id: employee.userId } })
                                    }}>
                                        <td>{employee.userId}</td>
                                        <td>{employee.firstName}</td>
                                        <td>{employee.gender}</td>
                                        <td>{employee.email}</td>
                                        <td>{employee.phoneNo}</td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            }
        </div>
    </div>
}

export default EmpAllOrders
