import { Link, useNavigate } from "react-router-dom"
import { useState } from "react"
import axios from "axios"
import { toast } from "react-toastify"
import './addFood.css'
import { URL } from "../../../../config"
import AdminHeader from "../../../../components/AdminHeader/adminHeader"


const AddFood = () => {

    const [name, setName] = useState('')
    const [image, setImage] = useState('')
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [quantity, setQuantity] = useState('')
    const { cuisineId } = state

    const navigate = useNavigate()

    const addFoodItem = () => {
        if (name.length == 0) {
            toast.warning('Enter Cuisine Name')
        }
        else if (image.length == 0) {
            toast.warning('Enter Image Link')
        }
        else if (description.length == 0) {
            toast.warning('Enter About Cuisine')
        }
        else if (price.length == 0) {
            toast.warning('Enter Price')
        }
        else if (quantity.length == 0) {
            toast.warning('Enter Quantity')
        }
        else {
            const body = {
                name,
                image,
                description,
                price,
                quantity
            }

            const url = `${URL}/admin/addfooditems`

            axios.post(url, body).then((response) => {
                const result = response.data

                // console.log(result)

                if (result['status'] === 'success') {
                    toast.success('Food Item Added')
                    navigate('/allFoods')
                }
                else {
                    toast.error(result['error'])
                }
            })
        }
    }

    return (
        <div>
            <AdminHeader></AdminHeader>
        </div>
    )
}

export default AddFood
