import axios from "axios"
import { useEffect, useState } from "react"
import { useLocation } from "react-router"
import { toast } from "react-toastify"
import AdminHeader from "../../../../components/AdminHeader/adminHeader"
import Food from "../../../../components/Food/food"
import { URL } from "../../../../config"
import './allFoods.css'

const AllFoods = () => {
    const [foods, setFoods] = useState([])
    const { state } = useLocation()

    const { id, cuisName } = state
    // console.log(id + cuisName)

    const getFoods = () => {

        const url = `${URL}/user/fooditems/${id}`

        axios.get(url).then((response) => {
            const result = response.data
            // console.log(result.data)
            if (result.status === 'success') {
                setFoods(result.data)
            } else {
                console.log(result.error)
                toast.error(result['error'])
            }
        })
    }

    useEffect(() => {
        getFoods()
    }, [])

    return <div className="container">
        <AdminHeader></AdminHeader>
        <div className="main-div">
            <div className="row">
                <div className="col">
                    <label className="emp-details">{cuisName} Cuisine</label>
                </div>
                <div className=" btn-group">
                    <button type="button" class="btn btn-outline-secondary btn-sm">Add New Food</button>
                    <button type="button" class="btn btn-outline-danger btn-sm">Delete Cuisine</button>
                </div>
            </div>
            {foods.length < 1 &&
                <div className="text-center fs-3 fw-light">No FoodItem To Show</div>
            }
           <div className="grid"> {
                foods.map((food) => {
                    return <div className="mb-3  "><Food food={food}></Food></div>
                })
            }</div>
        </div>
    </div>
}

export default AllFoods
