import axios from "axios"
import { useState } from "react"
import { useNavigate } from "react-router"
import { toast } from "react-toastify"
import user from '../../assets/user-icon.png'
import lock from '../../assets/lock-Icon.png'

const SiginModal = () => {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const navigate = useNavigate()

    const signinUser = () => {
        if (email.length == 0) {
            toast.warning('Enter Email')
        } else if (password.length == 0) {
            toast.warning('Enter Password')
        } else {
            const body = {
                email,
                password,
            }

            const url = `${URL}/user/signin`

            axios.post(url, body).then((response) => {
                const result = response.data
                // console.log(result)
                if (result['status'] == 'success') {
                    toast.success('Welcome to Emerald Oasis')

                    const { userId, firstName, lastName, roleId } = result['data']

                    sessionStorage['userId'] = userId
                    sessionStorage['roleId'] = roleId
                    sessionStorage['firstName'] = firstName
                    sessionStorage['lastName'] = lastName
                    sessionStorage['loginStatus'] = '1'

                    navigate('/homepage')
                } else {
                    toast.error(result['error'])
                }
            })
        }
    }


    return (
        <div>
            <label className="mb-4">Sign In</label>
            <div class="input-group mb-3">
                <img src={user} alt="" />
                <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" />
            </div>
            <div class="input-group mb-3">
                <img src={lock} alt="" />
                <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" />
            </div>
            <div>
                <button>L</button>
            </div>
        </div>
    )
}

export default SiginModal
