import './food.css'

const Food = (props) => {
    const { food, setFood } = props
    // console.log(food.name)

    const deleteFood=()=>{
        if(window.confirm("This Food Item will be Deleted Permanently\nAre You Sure?")){
          
            const url =`${URL}/admin/deletefooditem/${userDetails.userId}`

            axios.delete(url).then((response) => {
                const result = response.data
                if (result.status === 'success') {
                    toast.success("Employee Deleted")
                    navigate('/employees')
                } else {
                    console.log(result.error)
                    toast.error(result['error'])
                }
            })
        }
    }
    return (
        <div class="containerr">
            <div class="textt">{food.name}</div>
            <div className="row">
                <img src={food.image} alt="Avatar" class="col imagee" ></img>
                <div className="col " >{food.description}</div>
            </div>
            <div className="row mb-4">
                <div className="col ">Price:- Rs. {food.price}/-</div>
                <div className="col">QTY :-  {food.quantity}</div>
            </div>
            <div className="row">
                <div className="col btn-group ">
                    <button type="button" class="btn btn-outline-secondary ">EDIT</button>
                    <button type="button" class="btn btn-outline-secondary " onClick={deleteFood}>DELETE</button>
                </div>
            </div>
            <hr />
        </div>
    )
}

export default Food
