import { Link, Navigate, useLocation, useNavigate } from "react-router-dom"
import { useEffect, useState} from "react"
// import logo from '../assets/logo.png'
// import './manage.css'
import { toast } from "react-toastify"
import axios from "axios"



const EmpDetails = () => {
    const { state } = useLocation()
    const [empMoreDetails, setEmpMoreDetails] = useState([])
    const navigate = useNavigate()


    const getAll = () => {
        const url = `${URL}/admin/employees`
        axios.get(url).then((response) => {
            const result = response.data
            if(result['status'] == 'success') {
                setEmpMoreDetails(result['data'])
            } else {
                toast.error(result['error'])
            }
        })
    }
    
    useEffect(() => {
        getAll()
    }, [])

    const saveStatus = () => {

    }

    const saveSalary = () => {
        
    }



    
    
    
    
    
    
    
    return(
        <div className="margin">

            <div className="container">
                <div className="divstyle">
                    <h1 className="employeeh1">Employee Details</h1>
                </div>

                
                <form>
                    <div className="row">
                        <div className="form-group col-md-10">
                            <label for="inputId">Employee Id</label>
                            <input 
                            class="form-control" 
                            type="text" 
                            id="inputfirstName" 
                            value="1" 
                            aria-label="Disabled input example" disabled readonly    
                            />
                        </div>
                    </div>
                    <br/>
                    <div className="row">
                        <div className="form-group col-md-5">
                            <label for="inputfirstName">First Name</label>
                            <input 
                            class="form-control" 
                            type="text" 
                            id="inputfirstName" 
                            value="Abhijeet" 
                            aria-label="Disabled input example" disabled readonly    
                            />
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputlastName">Last Name</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputlastName" 
                            value="Kumkar" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                    </div>
                    <br/>
                        <div className="row">
                        <div className="form-group col-md-5">
                            <label for="inputEmail">Email</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputEmail" 
                            value="abhijeet09.ak@gmail.com" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        <div className="form-group col-md-5">
                            <label for="inputPhoneNumber">Phone Number</label>
                            <input
                            class="form-control" 
                            type="text" 
                            id="inputPhoneNumber" 
                            value="9970106353" 
                            aria-label="Disabled input example" disabled readonly
                            />
                        </div>
                        </div>
                        <br />
                        <div className="row">
