import { useState } from "react";
import { URL } from "../../../config";
import Header1 from "../../../components/Header1/header1";
import Sidebar from "../../../components/Sidebar/sidebar";
import Profile from "../../../components/Profile/profile";
import ChangePasswordComp from "../../../components/ChangePasswordComp/ChangePasswordComp";

const CustomerProfile = () => {
  const userId = 1; //get this value from session storage this is for testing purpose
  // const { userId } = sessionStorage;
  const [chngPswdStatus, setChngPswdStatus] = useState(false);
  const urlToSaveDetails = `${URL}/user/profile/savedetails`;
  const urlToGetDetails = `${URL}/user/profile/${userId}`;
  return (
    <>
      <div className="row">
        <Header1 />
        {/* Can I write logic to determine to keep the not operator here..Coz depending on this either the profile details are shown or change password can be seen */}
        <Profile
          userId={userId}
          urlToGetDetails={urlToGetDetails}
          urlToSaveDetails={urlToSaveDetails}
        />
        <Sidebar />
      </div>
    </>
  );
};

export default CustomerProfile;
